mapper.select:

SELECT id, teacher_no, teacher_name, teacher_dept, grade, title, major_name, class_no, major_name, depet_name, course_name, year, term
FROM evaluation , teacher, dept, user ,

WHERE is_alive = 0 AND title LIKE CONCAT('%',#{search},'%')
ORDER BY id ${sort} 
LIMIT #{offset},#{pageSize}

SELECT class_no
FROM student
WHERE student_no = #{student_no}

SELECT class_no
FROM 
class
WHERE 
	grade=#{grade}
AND
	class_index=#{classIndex}
AND 
	major_no=#{majorNo}
